import { createSlice } from '@reduxjs/toolkit';


const stockSlice = createSlice({
  name: 'stocks',
  initialState: [],
  reducers: {
    addState: (state, action) => {
      state.push(action.payload)
    }
  }
})

export const { addState } = stockSlice.actions

export default stockSlice.reducer;
