import { useGetStocksQuery } from '../redux/stockApiSlice';
import { Ticker } from './Ticker';
import { io } from 'socket.io-client';

export function Tickers () {

  const {
    data,
    isLoading,
    isSuccess,
    isError,
    error
  } = useGetStocksQuery()

  if (isLoading) {
    console.log('Loading...')
  } else if (isSuccess) {
    console.log(data.flat())
  } else if (isError) {
    console.log(error);
  }

  const socket = io('http://localhost:4000')

  const starter = (num) => {
    socket.emit('start', num)
  }

  return (
    <div className='container mx-auto'>
      <button className='btn' onClick={() => starter(1000)}>Start</button>
      {
        isSuccess ? 
        data.flat().map((item, i) => 
          <Ticker 
            key={i + 1}
            ticker={item.ticker}
            exchange={item.exchange}
            price={item.price}
            change={item.change}
            change_percent={item.change_percent}
          />
        )
        : 
        null

     }
    </div>
  )
}
