import { io } from 'socket.io-client';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { changeInterval } from '../redux/stockSlice';

export function Ticker ({
  ticker,
  exchange,
  price,
  change,
  change_percent
}) 
{

  const dispatch = useDispatch()


  return (
    <div className="row">
      <p className="col my-auto">Name: {ticker}</p>
      <p className="col text-end my-auto">Stock: {exchange}</p>
      <p className="col text-end my-auto">Price: {price}$</p>
      <p className="col text-end my-auto">Change: {change}$</p>
      <p className="col text-end my-auto">In percents: {change_percent}%</p>
      <button className="btn btn-danger col my-auto">Delete</button>
      <button className="btn btn-warning col my-auto">Start/Stop</button>
      {/* <button className="btn btn-info col my-auto w-25">Set Interval</button> */}
      <div className="dropdown col my-auto">
        <button className="btn btn-info dropdown-toggle" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
          Set Interval
        </button>
        <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
          <li><a className="dropdown-item" href="#" onClick={() => dispatch(changeInterval(1000))}>1 sec</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => dispatch(changeInterval(3000))}>3 sec</a></li>
          <li><a className="dropdown-item" href="#" onClick={() => dispatch(changeInterval(5000))}>5 sec</a></li>
        </ul>
      </div>
    </div>
  )
}
