import { createSlice } from '@reduxjs/toolkit';


const stocksSlice = createSlice({
  name: 'stocks',
  initialState: {
    interval: 5000,
    cachedTickers: [],
    newEntry: '',
    tickID: ''

  },
  reducers: {
    changeInterval: (state, action) => {
      state.interval = action.payload
    }, 
    setCachedTicker: (state, action) => {
      state.cachedTickers.push(action.payload)
    },
    setNewTicker: (state, action) => {
      if (state.newEntry !== action.payload) {
        state.newEntry = action.payload
      }
    },
    setTickId: (state, action) => {
      if (state.tickID !== action.payload) {
        state.tickID = action.payload
      }
    }

  }
})


export const { changeInterval, setCachedTicker, setNewTicker } = stocksSlice.actions
export default stocksSlice.reducer;
