import { useState } from "react"
import { setCachedTicker, clearCachedTickers } from "../redux/stockSlice"
import { useDispatch, useSelector } from "react-redux"

export function Ticker ({
  id,
  ticker,
  exchange,
  price,
  change,
  change_percent
}) 
{

  const dispatch = useDispatch()
  const { cachedTickers } = useSelector(state => state.stocks)

  const filteredTicker = cachedTickers.filter(tickr => tickr.id === id)

  const [clicked, setClicked] = useState(false)

  const handleCache = () => {
    const cachedTicker = {
      id,
      ticker,
      exchange,
      price,
      change,
      change_percent
    }
    setClicked(!clicked)
    if (!cachedTickers.map(tick => tick.id).includes(id)) {
      dispatch(setCachedTicker(cachedTicker))  
    }
    if (clicked === false) {
      dispatch(clearCachedTickers())
    }
    console.log(cachedTickers)
  }

  if (!clicked) {
    return (
      <div className='row'>
        <p className="col my-auto">Name: {ticker}</p>
        <p className="col text-end my-auto">Stock: {exchange}</p>
        <p className="col text-end my-auto">Price: {price}$</p>
        <p className="col text-end my-auto">Change: {change}$</p>
        <p className="col text-end my-auto">In percents: {change_percent}%</p>
        <button className="btn btn-danger col my-auto">Delete</button>
        <button className="btn btn-warning col my-auto" onClick={() => handleCache()}>Stop</button>
      </div> 
    )
  } else {
    return (
      <>
        {
          filteredTicker.map((tick, i) => 
            <div key={i} className='row'>
              <p className="col my-auto">Name: {tick.ticker}</p>
              <p className="col text-end my-auto">Stock: {tick.exchange}</p>
              <p className="col text-end my-auto">Price: {tick.price}$</p>
              <p className="col text-end my-auto">Change: {tick.change}$</p>
              <p className="col text-end my-auto">In percents: {tick.change_percent}%</p>
              <button className="btn btn-danger col my-auto">Delete</button>
              <button className="btn btn-warning col my-auto" onClick={() => handleCache()}>Start</button>
            </div>
          )
        }  
      </>
      )

  }

}
