import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';
import { io } from 'socket.io-client';

export const stockApiSlice = createApi({
  reducerPath: 'stockApiSlice',
  baseQuery: fetchBaseQuery({ baseUrl: '/'}),
  endpoints: builder => ({
    getStocks: builder.query({
      queryFn: () => ({ data: { tickers: [] }}),
      async onCacheEntryAdded(
        arg, 
        { updateCachedData, cacheDataLoaded, cacheEntryRemoved }
      ) {

        try {
          await cacheDataLoaded;
          const socket = io('http://localhost:4000');
          socket.emit('connection', () => {
            console.log('connected')
          })

          socket.emit('start', () => {
            console.log('started')

          })


          // const listener = (event) => {
          //   const data = JSON.parse(event.data)
          //   if ( data !== arg ) return

          //   updateCachedData((draft) => {
          //     draft.push(data)
          //     console.log(draft)
          //   })
          // }

          socket.on('ticker', (ticker) => {
            console.log(ticker)
            updateCachedData((currentCashData) => [...currentCashData, ticker])

          })
        } catch {

        }
        await cacheEntryRemoved;

      },
    }),
  }),
})

export const { useGetStocksQuery } = stockApiSlice;
